function cleanContentIds(sContent) {
  sContent = sContent.replace(/id=\"([^\"]*)\"/g, "");
  sContent = sContent.replace(/id=\'([^\']*)\'/g, "");
  sContent = sContent.replace(/id=([^ ]*)/g, "");
  return sContent;
};

