  this.Editor.setConfiguration({Field: this.Target, Dialogs: [{sName: DATE, Format: this.Format}]});
  this.Editor.setData({code:null,value:this.extValue.dom.value});
  this.Editor.onSelect = this.atSelect.bind(this);
