	@Test
	public void testLoadSpace() {
		client.core.model.Space space = createSpace();
		client.core.model.Account account = space.getAccount();

		assertEquals("monet", space.getName());
		assertEquals("Explorer Integration", space.getTitle());
		assertEquals("Explorer Integration Tests", space.getSubTitle());
		assertEquals("es", space.getLanguage());
		assertEquals("default", space.getTheme());
		assertEquals("English Text", space.getFederation().getLabel());
		assertEquals("http://localhost:8080/monet/explorer/api/model$file/?file=images/organization.logo.png", space.getFederation().getLogoUrl());
		assertEquals("http://10.13.13.145:8080/federation", space.getFederation().getUrl());
		assertEquals("admin", account.getFullName());
		assertEquals("admin@mock.com", account.getEmail());
		assertTrue(account.getRootNode() instanceof client.core.model.Desktop);
		assertEquals("localhost", space.getConfiguration().getDomain());
		assertEquals("http://localhost:8080/monet/explorer", space.getConfiguration().getUrl());
		assertEquals("http://localhost:8080/monet/explorer/api", space.getConfiguration().getApiUrl());
		assertEquals("http://localhost:8080/monet/explorer/push", space.getConfiguration().getPushUrl());
		assertEquals("http://localhost:8080/monet/analytics", space.getConfiguration().getAnalyticsUrl());
		assertEquals("http://localhost:8080/monet/explorer/digitalsignature", space.getConfiguration().getDigitalSignatureUrl());
		assertEquals("/monet/explorer/images", space.getConfiguration().getImagesPath());
		assertTrue(space.getTabs().size() > 0);
		assertTrue(space.getDictionary().getDefinitionCount() > 0);
	}

	@Test
	public void testThatEnvironmentTabHasEntityOfTypeNode() {
		client.core.model.Space space = createSpace();
		Space.Tab tab = space.getTabs().get(0);

		assertTrue(tab.getEntity() instanceof Node);
	}

