function objectEquals(obj, x) {
  var p = 0;

  for (p in obj) {
    if (typeof(x[p]) == 'undefined') {
      return false;
    }
  }

  for (p in obj) {
    if (obj[p]) {
      switch (typeof(obj[p])) {
        case 'object':
          if (!objectEquals(obj[p], x[p])) {
            return false;
          }
          break;
        case 'function':
          if (typeof(x[p]) == 'undefined' ||
              (p != 'equals' && obj[p].toString() != x[p].toString()))
            return false;
          break;
        default:
          if (obj[p] != x[p]) {
            return false;
          }
      }
    } else {
      if (x[p])
        return false;
    }
  }

  for (p in x) {
    if (typeof(obj[p]) == 'undefined') {
      return false;
    }
  }

  return true;
};

