	attribute.addIndicatorByValue(CGIndicator.FORMAT, 1, getFileExtension(State.AttachmentsViewer.getFilename()).toUpperCase());
	attribute.addIndicatorByValue(CGIndicator.DETAILS, 2, State.AttachmentsViewer.getLabel());

