	this.extCheckList.dom.innerHTML = WidgetWaitingTemplate.evaluate({'ImagesPath': Context.Config.ImagesPath, 'cls': CLASS_EDITOR_WAITING_MESSAGE, 'message': Lang.Editor.Dialogs.CheckReload.Reloading});
