    extWidget.dom.Id = Id;
    extWidget.dom.iOrder = this.aElementsWidgets.size();
    this.aElementsWidgets[Id] = WidgetFactory.get(extWidget.dom.getType(), extWidget);
    this.aElementsWidgets[Id].setWidgetRequired(this.WidgetRequired);
