CGDataLink.prototype.load = function (sLink, aParameters) {
  var request = new Request(sLink);

  for (var Index in aParameters) {
    if (isFunction(aParameters[Index])) continue;
    request.Add(Index, aParameters[Index]);
  }

  return request.Post();
};

